    public static List<Tile> fromC() {
        List<Tile> tiles = new ArrayList<>();
        char name = ' ';
        for (short[] serializedTile : cTiles) {
            int index = 0;
            Tile tile = new Tile(Character.toString(name++), null);
            for (short fourPixels : serializedTile) {
                for (int i = 0; i < 4; i++) {
                    int selectedColor = (fourPixels & (0xf << ((index % 4) * 4))) >> ((index % 4) * 4);
                    if (selectedColor == 1) selectedColor = 3;
                    if (selectedColor == 2) break;
                    tile.setTileData(index % 8, index / 8, selectedColor);
                    index++;
                }
                if (index % 4 != 0) break;
            }
            if (index != 64) continue;
            tiles.add(tile);
        }
        return tiles;
    }

